
public class _01 {

	/*
*	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			int a = Integer.parseInt(args[0]);
			if(a == 0)
			{
				System.out.println("equals 0");
			}else if(a<0)
			{
				System.out.println("under 0");
			}else if(a>0)
			{
				System.out.println("upper 0");
			}
					
			
	}

}
